		int &s=trie[p][x>>i&1];
		if(!s) s=++idx;
		p=s;
	}
}

//trie查询 
int trie_search(int x){
	int p=0;
	int ans=0;
	for(int i=bitsum;~i;i--){
		int s=x>>i&1;
		if(trie[p][!s]){
			ans+=1<<i;
			p=trie[p][!s];
		}else p=trie[p][s];
	}
	return ans;
}

int main()
{
	int x,y,w;
	cin>>n;
	for(int i=1;i<n;i++){
		cin>>x>>y>>w;
		add(x,y,w);
		add(y,x,w);
	}
	dfs(0,0);
	int ans=0;
//	for(int i=0;i<n;i++){
//		cout<<p[i]<<" ";
//	}
	for(int i=0;i<n;i++){
		trie_insert(p[i]);
	}

	for(int i=0;i<n;i++){
		ans=max(ans,trie_search(p[i]));
	}
	cout<<ans<<endl;
	return 0;
}
```

