		if (a[i] > 0) {
			cnt ++ ;
			res += a[i];
		}

	priority_queue<PII, vector<PII>, greater<PII>> heap;
	for (int i = 1; i <= n; i ++ ) { //初始化链表
		l[i] = i - 1;
		r[i] = i + 1;
		heap.push({abs(a[i]), i});
	}

	while (cnt > m) {
		while (st[heap.top().second]) heap.pop();
		auto t = heap.top();
		heap.pop();
		int v = t.first, p = t.second;
		if (l[p] != 0 && r[p] != n + 1 || a[p] > 0) {//判断边界 
			cnt -- ;
			res -= v;
			int left = l[p], right = r[p];
			a[p] += a[left] + a[right];
			heap.push({abs(a[p]), p});
			remove(left);
			remove(right);
		}
	}

	cout << res << endl;
	return 0;
}
```

